package java_dsa;
 import java.util.*;

public class if_else {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc=new Scanner(System.in);
				int age = sc.nextInt();
		
		
		if(age>18) {
			System.out.println("adult");
		}
		else {
			System.out.println("not adult");
		}

	}

}
