package java_dsa;

public class pattern3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=4;
		//outer loop
		for(int i=n;i>=1;i--) {
			//ineer loop
			for(int j=1;j<=i;j++) {
				System.out.print("*");
			}
			System.out.println();
			
		}
		

	}

}
